class simpleFETCH{get(e){return new Promise((t,n)=>{fetch(e).then(e=>e.json()).then(e=>t(e)).catch(e=>n(e))})}post(e,t){return new Promise((n,o)=>{fetch(e,{method:"POST",headers:{"Content-type":"application/json"},body:JSON.stringify(t)}).then(e=>e.json()).then(e=>n(e)).catch(e=>o(e))})}put(e,t){return new Promise((n,o)=>{fetch(e,{method:"PUT",headers:{"Content-type":"application/json"},body:JSON.stringify(t)}).then(e=>e.json()).then(e=>n(e)).catch(e=>o(e))})}delete(e){return new Promise((t,n)=>{fetch(e,{method:"DELETE",headers:{"Content-type":"application/json"}}).then(e=>e.json()).then(e=>t("Data Deleted!")).catch(e=>n(e))})}}
